import React from "react";
import { useRecoilValue, useSetRecoilState } from "recoil";
import { Categories, customCategoryState, IToDo, toDoState } from "../atoms";

function ToDo({ text, category, id }: IToDo) {
  const setToDos = useSetRecoilState(toDoState);
  const customCategory = useRecoilValue(customCategoryState);
  const onClick = (name: IToDo["category"]) => {
    setToDos((prev) => {
      const index = prev.findIndex((toDo) => toDo.id === id);

      const front = prev.slice(0, index);
      const back = prev.slice(index + 1);
      const newToDo = { text, id, category: name };
      const newArr = [...front, newToDo, ...back];
      return newArr;
    });
  };

  const onDelete = () => {
    setToDos((prev) => {
      const targetIndex = prev.findIndex((toDo) => toDo.id === id);
      const copyToDos = [...prev];
      copyToDos.splice(targetIndex, 1);
      return copyToDos;
    });
  };
  return (
    <>
      <li>
        <span>* {text}</span>
        {category !== Categories.DOING && (
          <button name={Categories.DOING} onClick={() => onClick(category)}>
            Doing
          </button>
        )}
        {category !== Categories.TODO && (
          <button name={Categories.TODO} onClick={() => onClick(category)}>
            To Do
          </button>
        )}
        {category !== Categories.DONE && (
          <button name={Categories.DONE} onClick={() => onClick(category)}>
            Done
          </button>
        )}
        <button onClick={onDelete}>X</button>
        {customCategory && customCategory.map((e) => <button>{e}</button>)}
      </li>
    </>
  );
}

export default ToDo;
